import { useState } from "react";
import "./styles.scss";
import DatePicker, { registerLocale } from "react-datepicker";
import es from "date-fns/locale/es";
import "react-datepicker/dist/react-datepicker.css";
import getYear from "date-fns/getYear";

registerLocale("es", es);

export default function App() {
    const [startDate, setStartDate] = useState(new Date());
    const years = [2020];
    return ( < DatePicker selected = { startDate }
            onChange = {
                (date) => setStartDate(date)
            }
            dateFormat = "dd/MM/yyyy"
            locale = "es"
            // En caso de que queremos que
            // Se escoja el mes y el año
            showYearDropdown showMonthDropdown scrollableYearDropdown yearDropdownItemNumber = { 5 }
            dropdownMode = "select"
            // La semana inciia desde Lunes
            calendarStartDay = { 1 }
            renderCustomHeader={({
                date,
                changeYear,
              }) => (
                  <select value={date.getYear()}
                    onChange={({ target: { value } }) => changeYear(value)}>
                    {years.map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
              )}

            />
        );


}
