import { useState } from "react";
import "./styles.scss";
import DatePicker, { registerLocale } from "react-datepicker";
import es from "date-fns/locale/es";
import "react-datepicker/dist/react-datepicker.css";

registerLocale("es", es);

export default function App() {
    const [startDate, setStartDate] = useState(new Date());
    const years = range(2022, getYear(new Date()) + 1, 1);
    return ( < DatePicker selected = { startDate }
            onChange = {
                (date) => setStartDate(date)
            }
            dateFormat = "dd/MM/yyyy"
            locale = "es"
            // En caso de que queremos que
            // Se escoja el mes y el año
            showYearDropdown showMonthDropdown scrollableYearDropdown yearDropdownItemNumber = { 5 }
            dropdownMode = "select"
            // La semana inciia desde Lunes
            calendarStartDay = { 1 }
            renderCustomHeader={({
                date,
                changeYear,
                changeMonth,
                decreaseMonth,
                increaseMonth,
                prevMonthButtonDisabled,
                nextMonthButtonDisabled,
              }) => (
                <div
                  style={{
                    margin: 10,
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <button onClick={decreaseMonth} disabled={prevMonthButtonDisabled}>
                    {"<"}
                  </button>
                  <select
                    value={getYear(date)}
                    onChange={({ target: { value } }) => changeYear(value)}
                  >
                    {years.map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>

                  <select
                    value={months[getMonth(date)]}
                    onChange={({ target: { value } }) =>
                      changeMonth(months.indexOf(value))
                    }
                  >
                    {months.map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>

                  <button onClick={increaseMonth} disabled={nextMonthButtonDisabled}>
                    {">"}
                  </button>
                </div>
              )}
              selected={startDate}
              onChange={(date) => setStartDate(date)}

                    />
                );
            };
